const App = () => {
  const categories = [
    {
      id: 1,
      title: 'Hats',
    },
  ];

  return (
    <div className='categories-container'>
      {categories.map((category) => {
        return (
          <div key={category.id} classname='category-container'>
            {/* <img /X */}
            <div className='category-body-container'>
              <h2>{ category.title }</h2>
              <p>Shop Now</p>
            </div>
          </div>
        );
      })}

      <div classname='category-container'>
        {/* <img /X */}
        <div className='category-body-container'>
          <h2>Jackets</h2>
          <p>Shop Now</p>
        </div>
      </div>
      <div classname='category-container'>
        {/* <img /X */}
        <div className='category-body-container'>
          <h2>Sneakers</h2>
          <p>Shop Now</p>
        </div>
      </div>
      <div classname='category-container'>
        {/* <img /X */}
        <div className='category-body-container'>
          <h2>Womens</h2>
          <p>Shop Now</p>
        </div>
      </div>
      <div classname='category-container'>
        {/* <img /X */}
        <div className='category-body-container'>
          <h2>Mens</h2>
          <p>Shop Now</p>
        </div>
      </div>
    </div>
  );
};

export default App;
